import { Component, EventEmitter, Input, Output } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { BookularService } from '../../services/bookular.service';
import { HttpClient } from '@angular/common/http';
import { IBook } from '../home/IBook';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-create-book',
  templateUrl: './create-book.component.html',
  styleUrls: ['./create-book.component.css']
})
export class CreateBookComponent {
/*  @Output() onSubmit: EventEmitter<IBook> = new EventEmitter();*/


  @Output() onSubmit = new EventEmitter<IBook>();

  onSubmit() {

    console.log("sdkfjsadfkjasndlfkasmndlfkasmdlf")
    const newBook: IBook = {
      id: 101,
      title: "Een nieuw boek toegevoegd",
      description: "Een nieuw boek om aan de lijst toe te voegen",
      author: {
        id: 567,
        firstName: 'Kees',
        lastName: 'Test',
        bio: 'Kees Test is een bijzondere auteur',
        books: null
      },
      authorId: 567
    };


    this.onSubmit.emit(newBook);
  }

  checkoutForm = this.formBuilder.group({
    id: '',
    title: ''
  });

  bookservice = new BookularService(this.http);
  constructor(
    private formBuilder: FormBuilder,
    private http: HttpClient
  ) { }
