import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { BookularService } from '../../services/bookular.service';
import { HttpClient } from '@angular/common/http';
import { IBook } from '../home/IBook';

@Component({
  selector: 'app-create-book',
  templateUrl: './create-book.component.html',
  styleUrls: ['./create-book.component.css']
})
export class CreateBookComponent {

  checkoutForm = this.formBuilder.group({
    id: '',
    title: ''
  });

  bookservice = new BookularService(this.http);
  constructor(
    private formBuilder: FormBuilder,
    private http: HttpClient
  ) { }

/*  onSubmit(): void {
    // Process checkout data here
    this.data = this.cartService.clearCart();
    console.warn('Your order has been submitted', this.checkoutForm.value);
    this.checkoutForm.reset();
  }*/

  onSubmit(): void {
    this.bookservice.books$.subscribe(books => {

      console.log(books.length);

      const newBook: IBook = {
        id: 101,
        title: "Een nieuw boek toegevoegd",
        description: "Een nieuw boek om aan de lijst toe te voegen",
        author: {
          id: 567,
          firstName: 'Kees',
          lastName: 'Test',
          bio: 'Kees Test is een bijzondere auteur',
          books: null
        },
        authorId: 567
      };

      books.push(newBook);

      console.log(books.length);
    });

    console.log("HELLO")

  }



}
