@Injectable({
  providedIn: 'root',
})
export class BookularService {
  private bookSubject = new BehaviorSubject<IBook[]>([]);
  books$ = this.bookSubject.asObservable();

  constructor(private http: HttpClient) {
 /*   this.getBooks("").subscribe((books) => {
