  }*/

  addBook(book: IBook): Observable<IBook> {
    return this.http.post<IBook>("https://localhost:7195/api/book/add", book);
  }

   getBooks(param: string) {
     return this.http
       .get<IBook[]>("https://localhost:7195/test.json");
       
   }
}
