import { Component, EventEmitter, Input, Output } from '@angular/core';
import { FormBuilder, FormControl, Validators } from '@angular/forms';
import { IBook } from '../home/IBook';

@Component({
  selector: 'app-create-book',
  templateUrl: './create-book.component.html',
  styleUrls: ['./create-book.component.css']
})
export class CreateBookComponent {
  @Output() onSubmit = new EventEmitter<IBook>();

  title = new FormControl('', [Validators.required]);
  description = new FormControl('', [Validators.required]);
  authorFirstName = new FormControl('', [Validators.required]);
  authorLastName = new FormControl('', [Validators.required]);
  authorBio = new FormControl('', [Validators.required]);

  checkoutForm = this.formBuilder.group({
    title: this.title,
    description: this.description,
    authorFirstName: this.authorFirstName,
    authorLastName: this.authorLastName,
    authorBio: this.authorBio
  });

  constructor(
    private formBuilder: FormBuilder
  ) {
  }

  addBook(): void {

    var formObject = this.checkoutForm.value;

    const newBook: IBook = {
      id: 0,
      title: formObject.title as string,
      description: formObject.description as string,
      author: {
        id: 0,
        firstName: formObject.authorFirstName as string,
        lastName: formObject.authorLastName as string,
        bio: formObject.authorBio as string,
        books: null
      },
      authorId: 567
    };

    this.onSubmit.emit(newBook as unknown as IBook);
  }
}

<div class="wrapper">
  <h5>Add Book</h5>
  <br />
  <form [formGroup]="checkoutForm" (ngSubmit)="addBook()">

    <div [hidden]="!title.hasError('required') || (title.dirty || title.touched)">
      The password is required.
    </div>

    <div class="row">
      <label class="column" for="title">
        Title
      </label>
      <input class="column" id="title" type="text" formControlName="title" required>

    </div>

    <div class="row">
      <label class="column" for="description">
        Description
      </label>
      <input class="column" id="description" type="text" formControlName="description" required>
    </div>

    <div class="row">
      <label class="column" for="authorFirstName">
        Author First Name
      </label>
      <input class="column" id="authorFirstName" type="text" formControlName="authorFirstName" required>
    </div>

    <div class="row">
      <label class="column" for="authorLastName">
        Author Last Name
      </label>
      <input class="column" id="authorLastName" type="text" formControlName="authorLastName" required>
    </div>

    <div class="row">
      <label class="column" for="authorBio">
        Author Bio
      </label>
      <input class="column" id="authorBio" type="text" formControlName="authorBio" required>
    </div>

    <br />
    <button [disabled]="!checkoutForm.valid" class="button" type="submit">Add Book</button>

  </form>
</div>
