import { Component, Input } from '@angular/core';
import { IBook } from '../home/IBook';
import { Observable } from 'rxjs/internal/Observable';
import { BookularService } from '../../services/bookular.service';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'book-table',
  templateUrl: './book-table.component.html',
  styleUrls: ['./book-table.component.css'],
})
export class BookTableComponent {
  @Input()
  data: Observable<IBook[]>;
  bookservice = new BookularService(this.http);

  constructor(private http: HttpClient) {
    this.data = new Observable<IBook[]>();
  }

  addBook(newBook: IBook) {
    this.data.subscribe(books => {
      books.push(newBook);
    });
  }
}
<table class="table" *ngIf="data">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">Title</th>
      <th scope="col">Author</th>
      <th scope="col">Description</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let book of data | async">
      <th scope="row">{{ book.id }}</th>
      <td>{{ book.title }}</td>
      <td>{{ book.author.firstName + " " + book.author.lastName }}</td>
      <td>{{ book.description }}</td>
    </tr>
  </tbody>
</table>

<br>

<app-create-book (onSubmit)="addBook($event)"></app-create-book>
