import { Component, Input } from '@angular/core';
import { IBook } from '../home/IBook';
import { Observable } from 'rxjs/internal/Observable';
import { tap } from 'rxjs';

@Component({
  selector: 'book-table',
  templateUrl: './book-table.component.html',
  styleUrls: ['./book-table.component.css'],
})
export class BookTableComponent {
  @Input()
  data: Observable<IBook[]>;

  constructor() {
    this.data = new Observable<IBook[]>();
  }

  onSave() {
    this.data.pipe(tap(usersList => {

      let newBook: IBook = {
          id: 101,
          title: "Een nieuw boek toegevoegd",
          description: "Een nieuw boek om aan de lijst toe te voegen",
          author: {
              id: 567,
              firstName: 'Kees',
              lastName: 'Test',
              bio: 'Kees Test is een bijzondere auteur',
              books: null
          },
          authorId: 567
      };
      
      usersList.push(new IBook);
    }));
    
    console.log("HELLO")
  }
}
<table class="table" *ngIf="data">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">Title</th>
      <th scope="col">Author</th>
      <th scope="col">Description</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let book of data | async">
      <th scope="row">{{ book.id }}</th>
      <td>{{ book.title }}</td>
      <td>{{ book.author.firstName + " " + book.author.lastName }}</td>
      <td>{{ book.description }}</td>
    </tr>
  </tbody>
</table>
<button (click)="onSave()">Save</button>
